mapper.select:
select * from sys_user_permission





		order by ${orderString}


		order by id DESC



	limit #{start}, #{length}

mapper.update:
update sys_user_permission
`parent_id` = #{parentId}, `parent_name` = #{parentName}, `name` = #{name}, `type` = #{type}, `url` = #{url}, `permission_code` = #{permissionCode}, `icon` = #{icon}, `order_num` = #{orderNum}, `is_deleted` = #{isDeleted}, `modification_by` = #{modificationBy}, `modification_ip` = #{modificationIp}, `remark` = #{remark}
where id = #{id} and is_deleted = 0

mapper.delete:
delete from sys_user_permission where id in 

	#{id}

and is_deleted = 0

mapper.select:
select count(*) from sys_user_permission where is_deleted = 0

	and permission_code = #{permissionCode}


	and id != #{id}
