sql.where:
and is_deleted = 0


        and area_id = #{extraSearch.areaId}


        and business_type_id = #{extraSearch.businessTypeId}


        and file_type = #{extraSearch.fileType}


        and filename like CONCAT('%',#{extraSearch.filename},'%')


        and file_desc like CONCAT('%',#{extraSearch.filedesc},'%')



        and collect_time >= #{extraSearch.collectTimeBegin}


        and collect_time < date_add(#{extraSearch.collectTimeEnd}, interval 1 day)

mapper.select:
select * from (select a.*, IFNULL(b.name,'根') as business_type_name from intelligence_import a left join
intelligence_business_type b on a.business_type_id=b.id) ab





        order by ${orderString}


        order by id DESC



    limit #{start}, #{length}

mapper.update:
update intelligence_import
`business_type_id` = #{businessTypeId}, `area_id` = #{areaId}, `file_type` = #{fileType}, `filename` = #{filename}, `file_url` = #{fileUrl}, `file_desc` = #{fileDesc}, `collect_time` = #{collectTime}, `es_index_name` = #{esIndexName}, `is_deleted` = #{isDeleted}, `modification_time` = #{modificationTime}, `modification_by` = #{modificationBy}, `modification_ip` = #{modificationIp}, `remark` = #{remark}
where id = #{id} and is_deleted = 0
