sql.where:
i.is_deleted=0


		and i.area_id = #{extraSearch.areaId}


		and i.reporting_time >= #{extraSearch.reportingTimeStart}


		and i.reporting_time < date_add( #{extraSearch.reportingTimeEnd}, interval 1 day)


		and i.intelligence_officer_id = #{extraSearch.intelligenceOfficerId}


		and io.is_officer =   ${extraSearch.isOfficer}

mapper.select:
SELECT t2.*, io.id_numbers, io. NAME, io.is_officer FROM ( SELECT intelligence_officer_id, SUM(count1) AS sum1, SUM(count2) AS sum2, SUM(count3) AS sum3, count(*) AS counts FROM ( SELECT intelligence_officer_id, CASE LEVEL WHEN 0 THEN 1 ELSE 0 END AS count1, CASE LEVEL WHEN 1 THEN 1 ELSE 0 END AS count2, CASE LEVEL WHEN 2 THEN 1 ELSE 0 END AS count3 FROM intelligence i
) t GROUP BY intelligence_officer_id ) t2 LEFT JOIN intelligence_officer io ON io.id = t2.intelligence_officer_id
order by ${orderString}
order by id DESC
limit #{start}, #{length}
