mapper.select:
select * from (select t1.*,t2.branch_name from logistics_record t1 left join logistics_user t2 on t1.user_id=t2.id) t





        order by ${orderString}


        order by id DESC



    limit #{start}, #{length}

mapper.update:
update logistics_record
`area_id` = #{areaId}, `business_type_id` = #{businessTypeId}, `file_type` = #{fileType}, `details_url` = #{detailsUrl}, `collect_time` = #{collectTime}, `es_index_name` = #{esIndexName}, `user_id` = #{userId}, `sender_id_card_num` = #{senderIdCardNum}, `sender_name` = #{senderName}, `sender_phone` = #{senderPhone}, `receiver_name` = #{receiverName}, `receiver_phone` = #{receiverPhone}, `receiver_address` = #{receiverAddress}, `goods_name` = #{goodsName}, `remark` = #{remark}, `is_deleted` = #{isDeleted}, `modification_by` = #{modificationBy}, `modification_ip` = #{modificationIp}
where id = #{id} and is_deleted = 0
